    const parentValues = JsUtil.flatFilter(answersObject, n => { return n.key === growingParent.id })
    if (!_.isEmpty(parentValues)) {
      _.remove(parentValues[0].value, row => { return row.key === idOfFieldToRemove })
    }
  }
}



